import React from 'react';
import MyChart from './MyChart';
import { useNavigate } from 'react-router-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './components/main.css';

const localizer = momentLocalizer(moment);

const MainMenu = () => {
  const navigate = useNavigate();

  return (
    <div className="main-container">
      <header className="header">
        <div className="dropdown-container">
          {/* Dropdown for assigning both Conductor and Inspector */}
          <select className="dropdown">
            <option value="">Assign Conductor & Inspector</option>
            <option value="conductor1-inspector1">Conductor 1 & Inspector 1</option>
            <option value="conductor2-inspector2">Conductor 2 & Inspector 2</option>
            <option value="conductor1-inspector2">Conductor 1 & Inspector 2</option>
            <option value="conductor2-inspector1">Conductor 2 & Inspector 1</option>
          </select>

          {/* Placeholder for the second dropdown (Editing inspection points) */}
          <select className="dropdown">
            <option value="">Edit Inspection Points</option>
            {/* Options for editing will be added here later */}
          </select>
        </div>
        <div className="profile-container" onClick={() => navigate('/profile')}>
          <img src="/profile.png" alt="Profile" className="profile-pic" />
        </div>
      </header>

      <div className="cards-container">
        <div className="card linechart-card">
          <MyChart />
        </div>
        <div className="card calendar-card">
          <h3>Calendar</h3>
          <div style={{ width: '100%', overflow: 'hidden' }}>
            <Calendar
              localizer={localizer}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 'auto', minHeight: '600px', margin: '20px' }} // Adjusted minHeight
              views={['month', 'week', 'day']}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainMenu;
