// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import MainMenu from './components/MainMenu';
import ChartComponent from './ChartComponent'; // Import ChartComponent

const App = () => {
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';

  return (
    <Router>
      <Routes>
        {/* Show Login if not authenticated, otherwise show MainMenu */}
        <Route path="/" element={isAuthenticated ? <Navigate to="/main" /> : <Login />} />
        <Route path="/main" element={isAuthenticated ? <MainMenu /> : <Navigate to="/" />} />
        {/* Add a new route to render ChartComponent */}
        <Route path="/chart" element={isAuthenticated ? <ChartComponent /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
