import React from 'react';
import { Line } from 'react-chartjs-2';
import './MainMenu.css';
import { useNavigate } from 'react-router-dom';

const MainMenu = () => {
  const navigate = useNavigate();

  // Sample line graph data
  const data = {
    labels: ['Trip 1', 'Trip 2', 'Trip 3', 'Trip 4', 'Trip 5'],
    datasets: [
      {
        label: 'Passengers',
        data: [25, 30, 22, 27, 33],
        borderColor: '#717D84',
        backgroundColor: '#C9D1D8',
        fill: true,
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="main-container">
      <header className="header">
        <div className="dropdown-container">
          <select className="dropdown">
            <option>Assign Conductor</option>
            <option>Conductor 1</option>
            <option>Conductor 2</option>
          </select>
          <select className="dropdown">
            <option>Assign Inspector</option>
            <option>Inspector 1</option>
            <option>Inspector 2</option>
          </select>
        </div>
        <div className="profile-container" onClick={() => navigate('/profile')}>
          <img src="/path/to/profile.png" alt="Profile" className="profile-pic" />
        </div>
      </header>

      <div className="cards-container">
        <div className="card">
          <h3>Passenger Statistics</h3>
          <Line data={data} />
        </div>

        <div className="card calendar-card">
          <h3>Calendar</h3>
          <iframe src="https://calendar.google.com/calendar/embed?..." style={{ border: '0', width: '100%', height: '100%' }}></iframe>
        </div>
      </div>
    </div>
  );
};

export default MainMenu;
