import React, { useState } from 'react';
import MyChart from './MyChart';  // Assuming you have a MyChart component
import Calendar from 'react-big-calendar'; // Assuming you use react-big-calendar

const MainPage = () => {
  const [showModal, setShowModal] = useState(false);
  const [assignments, setAssignments] = useState([]);
  const [conductors, setConductors] = useState([]);
  const [inspectors, setInspectors] = useState([]);
  const [inspectionPoints, setInspectionPoints] = useState([]);
  const [selectedConductor, setSelectedConductor] = useState('');
  const [selectedInspector, setSelectedInspector] = useState('');
  const [selectedInspectionPoint, setSelectedInspectionPoint] = useState('');
  const events = []; // Placeholder for calendar events
  const localizer = {}; // Placeholder for calendar localizer

  const handleAssignment = () => {
    // Handle assignment logic here
  };

  const handleClearAssignments = () => {
    // Logic to clear assignments
    setAssignments([]);
  };

  return (
    <div className="main-container">
      <header className="header">
        <button className="assign-button" onClick={() => setShowModal(true)}>
          Assign Roles
        </button>
      </header>

      <div className="cards-container">
        {/* Line Graph Container */}
        <div className="card linechart-card">
          <MyChart />
        </div>

        {/* Assigned Personnel Container - Moved below the Line Graph */}
        <div className="card assigned-table">
          <h3>Assigned Personnel</h3>
          <table>
            <thead>
              <tr>
                <th>Conductor</th>
                <th>Inspector</th>
                <th>Inspection Point</th>
              </tr>
            </thead>
            <tbody>
              {assignments.map((assignment, idx) => (
                <tr key={idx}>
                  <td>{assignment.conductor_name}</td>
                  <td>{assignment.inspector_name}</td>
                  <td>{assignment.inspection_point}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={handleClearAssignments}>Clear Assignments</button>
        </div>

        {/* Calendar Container */}
        <div className="card calendar-card">
          <h3>Calendar</h3>
          <div style={{ width: '100%', overflow: 'hidden' }}>
            <Calendar
              localizer={localizer}
              events={events}
              startAccessor="start"
              endAccessor="end"
              style={{ height: '500px' }}
              views={['month', 'week', 'day']}
            />
          </div>
        </div>
      </div>

      {/* Modal for assigning roles */}
      <div className={`modal-overlay ${showModal ? 'show' : ''}`}>
        <div className="modal-content">
          <h3>Assign Conductor, Inspector & Inspection Point</h3>

          <div className="dropdown-container">
            <select className="dropdown" onChange={(e) => setSelectedConductor(e.target.value)}>
              <option value="">Select Conductor</option>
              {conductors.map((conductor) => (
                <option key={conductor.id} value={conductor.id}>
                  {conductor.name}
                </option>
              ))}
            </select>

            <select className="dropdown" onChange={(e) => setSelectedInspector(e.target.value)}>
              <option value="">Select Inspector</option>
              {inspectors.map((inspector) => (
                <option key={inspector.id} value={inspector.id}>
                  {inspector.name}
                </option>
              ))}
            </select>

            <select className="dropdown" onChange={(e) => setSelectedInspectionPoint(e.target.value)}>
              <option value="">Select Inspection Point</option>
              {inspectionPoints.map((point) => (
                <option key={point.id} value={point.id}>
                  {point.name}
                </option>
              ))}
            </select>
          </div>

          <div className="dropdown-row">
            <button className="assign-button" onClick={handleAssignment}>Assign</button>
            <button className="close-modal" onClick={() => setShowModal(false)}>Close</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainPage;
