import React, { useState } from 'react';

const MainMenu = () => {
  const [showModal, setShowModal] = useState(false);

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="main-container">
      <header className="header">
        <div className="dropdown-container">
          <button className="assign-button" onClick={openModal}>
            Assign Conductor & Inspector
          </button>
          <button className="edit-button">Edit Inspection Points</button>
        </div>
        <div className="profile-container">
          <img src="/profile.png" alt="Profile" className="profile-pic" />
        </div>
      </header>

      <div className={`modal-overlay ${showModal ? 'show' : ''}`}>
        <div className="modal-content">
          <h3>Assign Conductor & Inspector</h3>
          <select className="dropdown">
            <option>Conductor 1</option>
            <option>Conductor 2</option>
          </select>
          <select className="dropdown">
            <option>Inspector 1</option>
            <option>Inspector 2</option>
          </select>
          <button className="close-modal" onClick={closeModal}>Close</button>
        </div>
      </div>

      {/* Other components like cards and calendar can go here */}
    </div>
  );
};

export default MainMenu;
