import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './MainPage';
import Login from './Login';

function App() {
  const isAuthenticated = !!localStorage.getItem('token'); // Simple token check for demo purposes

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/main" element={isAuthenticated ? <MainPage /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to={isAuthenticated ? "/main" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
