import React, { useState, useEffect } from 'react';
import MyChart from './MyChart';
import { useNavigate } from 'react-router-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './components/main.css';
import { assignConductorInspector } from './api'; // Import the API function

const localizer = momentLocalizer(moment);

const MainMenu = () => {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false); // Confirmation modal state
  const [selectedConductor, setSelectedConductor] = useState('');
  const [selectedInspector, setSelectedInspector] = useState('');
  const [conductors, setConductors] = useState([]);
  const [inspectors, setInspectors] = useState([]);

  // Fetch conductors and inspectors when the modal opens
  useEffect(() => {
    if (showModal) {
      fetch('http://localhost:5000/conductors')
        .then(response => response.json())
        .then(data => setConductors(data))
        .catch(error => console.error('Error fetching conductors:', error));

      fetch('http://localhost:5000/inspectors')
        .then(response => response.json())
        .then(data => setInspectors(data))
        .catch(error => console.error('Error fetching inspectors:', error));
    }
  }, [showModal]); // Only fetch data when the modal opens

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  const handleAssignment = async () => {
    try {
      const response = await assignConductorInspector(selectedConductor, selectedInspector);
      console.log(response); // Handle the successful response

      // Show confirmation modal
      setShowModal(false); // Close assign modal
      setShowConfirmation(true); // Show confirmation modal
    } catch (error) {
      console.error('Error assigning:', error);
      alert('There was an error assigning the conductor and inspector.');
    }
  };

  const closeConfirmation = () => {
    setShowConfirmation(false);
  };

  return (
    <div className="main-container">
      <header className="header">
        <div className="button-container">
          <button className="assign-button" onClick={toggleModal}>Assign Conductor & Inspector</button>
          <button className="edit-button" disabled>Edit Inspection Points</button>
        </div>
        <div className="profile-container" onClick={() => navigate('/profile')}>
          <img src="/profile.png" alt="Profile" className="profile-pic" />
        </div>
      </header>

      <div className="cards-container">
        <div className="card linechart-card">
          <MyChart />
        </div>
        <div className="card calendar-card">
          <h3>Calendar</h3>
          <div style={{ width: '100%', overflow: 'hidden' }}>
            <Calendar
              localizer={localizer}
              startAccessor="start"
              endAccessor="end"
              style={{ height: 'auto', minHeight: '600px', margin: '20px' }}
              views={['month', 'week', 'day']}
            />
          </div>
        </div>
      </div>

      {/* Assign Modal */}
      <div className={`modal-overlay ${showModal ? 'show' : ''}`}>
        <div className="modal-content">
          <h3>Assign Conductor & Inspector</h3>

          {/* Dropdown for Conductors */}
          <select className="dropdown" onChange={(e) => setSelectedConductor(e.target.value)}>
            <option value="">Select Conductor</option>
            {conductors.map(conductor => (
              <option key={conductor.id} value={conductor.id}>
                {conductor.name}
              </option>
            ))}
          </select>

          {/* Dropdown for Inspectors */}
          <select className="dropdown" onChange={(e) => setSelectedInspector(e.target.value)}>
            <option value="">Select Inspector</option>
            {inspectors.map(inspector => (
              <option key={inspector.id} value={inspector.id}>
                {inspector.name}
              </option>
            ))}
          </select>

          <button className="assign-button" onClick={handleAssignment}>Assign</button>
          <button className="close-modal" onClick={toggleModal}>Close</button>
        </div>
      </div>

      {/* Confirmation Modal */}
<div className={`modal-overlay ${showConfirmation ? 'show' : ''}`}>
  <div className="modal-content">
    <h3>Assignment Successful!</h3>
    <p>Conductor: {selectedConductor}</p>
    <p>Inspector: {selectedInspector}</p>
    <button className="close-modal" onClick={() => setShowConfirmation(false)}>Close</button>
  </div>
</div>

    </div>
  );
};

export default MainMenu;
