import React, { useState, useEffect } from 'react';

const ConductorsAndInspectors = () => {
  const [conductors, setConductors] = useState([]);
  const [newConductorName, setNewConductorName] = useState('');
  const [updateConductorId, setUpdateConductorId] = useState('');
  const [updateConductorName, setUpdateConductorName] = useState('');

  useEffect(() => {
    // If no data in localStorage, set default conductors
    if (!localStorage.getItem('conductors')) {
      const defaultConductors = [
        { id: 1, name: 'Conductor A' },
        { id: 2, name: 'Conductor B' },
      ];
      localStorage.setItem('conductors', JSON.stringify(defaultConductors));
    }

    // Fetch data from localStorage
    setConductors(JSON.parse(localStorage.getItem('conductors')));
  }, []);

  const handleAddConductor = () => {
    const conductors = JSON.parse(localStorage.getItem('conductors')) || [];
    const newId = conductors.length ? conductors[conductors.length - 1].id + 1 : 1;
    const newConductor = { id: newId, name: newConductorName };
    conductors.push(newConductor);
    localStorage.setItem('conductors', JSON.stringify(conductors));
    setConductors(conductors);
    setNewConductorName('');
  };

  const handleUpdateConductor = () => {
    const updatedConductors = conductors.map(conductor => {
      if (conductor.id === parseInt(updateConductorId)) {
        return { ...conductor, name: updateConductorName };
      }
      return conductor;
    });
    localStorage.setItem('conductors', JSON.stringify(updatedConductors));
    setConductors(updatedConductors);
    setUpdateConductorId('');
    setUpdateConductorName('');
  };

  const handleDeleteConductor = (id) => {
    const filteredConductors = conductors.filter(conductor => conductor.id !== id);
    localStorage.setItem('conductors', JSON.stringify(filteredConductors));
    setConductors(filteredConductors);
  };

  return (
    <div>
      <h1>Conductors</h1>
      <input
        type="text"
        placeholder="Enter Conductor Name"
        value={newConductorName}
        onChange={(e) => setNewConductorName(e.target.value)}
      />
      <button onClick={handleAddConductor}>Add Conductor</button>

      <input
        type="text"
        placeholder="Conductor ID to update"
        value={updateConductorId}
        onChange={(e) => setUpdateConductorId(e.target.value)}
      />
      <input
        type="text"
        placeholder="New Conductor Name"
        value={updateConductorName}
        onChange={(e) => setUpdateConductorName(e.target.value)}
      />
      <button onClick={handleUpdateConductor}>Update Conductor</button>

      <ul>
        {conductors.map((conductor) => (
          <li key={conductor.id}>
            {conductor.name}
            <button onClick={() => handleDeleteConductor(conductor.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ConductorsAndInspectors;
