// src/ChartComponent.js

import React, { useEffect } from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Register the required components
ChartJS.register(
  CategoryScale, // For x-axis categories
  LinearScale,   // For y-axis
  BarElement,    // For bar charts (replace with LineElement for line charts)
  Title,
  Tooltip,
  Legend
);

const ChartComponent = () => {
  const data = {
    labels: ['January', 'February', 'March', 'April', 'May'],
    datasets: [
      {
        label: 'Dataset 1',
        data: [10, 20, 30, 40, 50],
        backgroundColor: '#71BCEB',
      }
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Sample Chart',
      },
    },
  };

  useEffect(() => {
    return () => {
      ChartJS.getChart('0')?.destroy(); // Cleanup on unmount
    };
  }, []);

  return <Bar id="0" data={data} options={options} />;
};

export default ChartComponent;
