// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import MainMenu from './components/MainMenu';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const authStatus = localStorage.getItem('isAuthenticated') === 'true';
    setIsAuthenticated(authStatus);
  }, []);

  const handleLogin = () => {
    // Perform login logic, and on success:
    localStorage.setItem('isAuthenticated', 'true');
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <Routes>
        {/* Show Login if not authenticated, otherwise show MainMenu */}
        <Route path="/" element={isAuthenticated ? <Navigate to="/main" /> : <Login onLogin={handleLogin} />} />
        <Route path="/main" element={isAuthenticated ? <MainMenu /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
